mapper.select:
SELECT
	`id`, 
	`uuid`, 
	`date`, 
	`fromHeadpic`, 
	`oldTxt`, 
	`toUid`, 
	`chatid`, 
	`psr`, 
	`txt`, 
	`aite`, 
	`fromUid`, 
	`fromName`, 
	`simple_content`, 
	`createDate`, 
	`modifyDate`
FROM
	message_history
where
1=1
 and `id` = #{id} 
 and `uuid` = #{uuid} 
 and `date` = #{date} 
 and `fromHeadpic` = #{fromHeadpic} 
 and `oldTxt` = #{oldTxt} 
 and `toUid` = #{toUid} 
 and `chatid` = #{chatid} 
 and `psr` = #{psr} 
 and `txt` = #{txt} 
 and `aite` = #{aite} 
 and `fromUid` = #{fromUid} 
 and `fromName` = #{fromName} 
 and `simple_content` = #{simple_content} 
 and `createDate` = #{createDate} 
 and `modifyDate` = #{modifyDate} 
ORDER BY
	id DESC

mapper.delete:
DELETE
FROM
message_history
WHERE
fromUid= #{uid}
or toUid= #{uid}
