mapper.select:
SELECT
	r.`id`,
	r.`create_date` createDate,
	r.`modify_date` modifyDate,
	r.`descri`,
	r.`head_img` headImg,
	r.`name`,
	r.`opener_price` openerPrice,
	r.`send_price` sendPrice,
	r.`status`,
	r.`control_game` controlGame,
	r.`control_model` controlModel,
	r.`game_type` gameType,
	r.`limit_num` limitNum,
	r.`open_fee_add` openFeeAdd,
	r.`open_fee_sum` openFeeSum,
	r.`owner`,
	r.`player_win_rate_s1` playerWinRateS1,
	r.`psw`,
	r.`send_count` sendCount,
	r.`send_fee_add` sendFeeAdd,
	r.`send_fee_sum` sendFeeSum,
	r.``un_dead` unDead,`,
	r.`player_open_win_rate_s1` playerOpenWinRateS1,
	r.`player_send_win_rate_s1` playerSendWinRateS1,
	r.`room_id` roomId,
	r.`props`,
	r.`subname`,
	r.`fixed_robot_ids` fixedRobotIds,
	r.`win_fee_add` winFeeAdd,
	r.`win_fee_sum` winFeeSum,
	r.`rule_pic` rulePic,
	r.`game_status` gameStatus,
	r.`end_date` endDate,
	r.`owner_uuid` ownerUuid,
	r.`independence`,
	r.`agent_red_fan_dian` agentRedFanDian,
	r.`max_robot_count` maxRobotCount,
	r.`open_red_delay_end` openRedDelayEnd,
	r.`open_red_delay_start` openRedDelayStart,
	r.`fateqq_codepay_id` fateqqCodepayId,
	r.`fateqq_key` fateqqKey,
	r.`fateqq_token` fateqqToken,
	r.`domain`,
	r.`robot_rule` robotRule,
	r.`member_ids` memberIds,
	r.`stopspeak_member_ids`,
	r.`member_mgr_ids` memberMgrIds,
	r.`yaoqing_able` yaoqingAble,
	r.`yaoqing_audit_able` yaoqingAuditAble,
	r.`use_custom_headpic` useCustomHeadpic,
	r.`org_id` orgId,
	m.`nick_name` ownerName
FROM
	room r left join member m on r.owner_UUID=m.id
where
1=1
 and `id` = #{id} 
 and `create_date` = #{createDate} 
 and `modify_date` = #{modifyDate} 
 and `descri` = #{descri} 
 and `head_img` = #{headImg} 
 and `name` = #{name} 
 and `opener_price` = #{openerPrice} 
 and `sendPrice` = #{sendPrice} 
 and `status` = #{status} 
 and `control_game` = #{controlGame} 
 and `control_model` = #{controlModel} 
 and `game_type` = #{gameType} 
 and `limit_num` = #{limitNum} 
 and `open_fee_add` = #{openFeeAdd} 
 and `open_fee_sum` = #{openFeeSum} 
 and `owner` = #{owner} 
 and `player_win_rate_s1` = #{playerWinRateS1} 
 and `psw` = #{psw} 
 and `send_count` = #{sendCount} 
 and `send_fee_add` = #{sendFeeAdd} 
 and `send_fee_sum` = #{sendFeeSum} 
 and `un_dead` = #{unDead} 
 and `player_open_win_rate_s1` = #{playerOpenWinRateS1} 
 and `player_send_win_rate_s1` = #{playerSendWinRateS1} 
 and `room_id` = #{roomId} 
 and `props` = #{props} 
 and `subname` = #{subname} 
 and `fixed_robot_ids` = #{fixedRobotIds} 
 and `win_fee_add` = #{winFeeAdd} 
 and `win_fee_sum` = #{winFeeSum} 
 and `rule_pic` = #{rulePic} 
 and `game_status` = #{gameStatus} 
 and `end_date` = #{endDate} 
 and `owner_uuid` = #{ownerUuid} 
 and `independence` = #{independence} 
 and `agent_red_fan_dian` = #{agentRedFanDian} 
 and `max_robot_count` = #{maxRobotCount} 
 and `open_red_delay_end` = #{openRedDelayEnd} 
 and `open_red_delay_start` = #{openRedDelayStart} 
 and `fateqq_codepay_id` = #{fateqqCodepayId} 
 and `fateqq_key` = #{fateqqKey} 
 and `fateqq_token` = #{fateqqToken} 
 and `domain` = #{domain} 
 and `robot_rule` = #{robotRule} 
 and `member_ids` = #{memberIds} 
 and `stopspeak_member_ids` = #{stopspeakMemberIds} 
 and `member_mgr_ids` = #{memberMgrIds} 
 and `yaoqing_able` = #{yaoqingAble} 
 and `yaoqing_audit_able` = #{yaoqingAuditAble} 
 and `use_custom_headpic` = #{useCustomHeadpic} 
 and r.`org_id` = #{orgId} 
 and m.`nick_name` = #{ownerName} 
ORDER BY
	id DESC

mapper.update:
UPDATE room
`create_date` = #{createDate}, `modify_date` = #{modifyDate}, `descri` = #{descri}, `head_img` = #{headImg}, `name` = #{name}, `opener_price` = #{openerPrice}, `sendPrice` = #{sendPrice}, `status` = #{status}, `control_game` = #{controlGame}, `control_model` = #{controlModel}, `game_type` = #{gameType}, `limit_num` = #{limitNum}, `open_fee_add` = #{openFeeAdd}, `open_fee_sum` = #{openFeeSum}, `owner` = #{owner}, `player_win_rate_s1` = #{playerWinRateS1}, `psw` = #{psw}, `send_count` = #{sendCount}, `send_fee_add` = #{sendFeeAdd}, `send_fee_sum` = #{sendFeeSum}, `un_dead` = #{unDead}, `player_open_win_rate_s1` = #{playerOpenWinRateS1}, `player_send_win_rate_s1` = #{playerSendWinRateS1}, `room_id` = #{roomId}, `props` = #{props}, `subname` = #{subname}, `fixed_robot_ids` = #{fixedRobotIds}, `win_fee_add` = #{winFeeAdd}, `win_fee_sum` = #{winFeeSum}, `rule_pic` = #{rulePic}, `game_status` = #{gameStatus}, `end_date` = #{endDate}, `owner_uuid` = #{ownerUuid}, `independence` = #{independence}, `agent_red_fan_dian` = #{agentRedFanDian}, `max_robot_count` = #{maxRobotCount}, `open_red_delay_end` = #{openRedDelayEnd}, `open_red_delay_start` = #{openRedDelayStart}, `fateqq_codepay_id` = #{fateqqCodepayId}, `fateqq_key` = #{fateqqKey}, `fateqq_token` = #{fateqqToken}, `domain` = #{domain}, `robot_rule` = #{robotRule}, `member_ids` = #{memberIds}, `stopspeak_member_ids` = #{stopspeakMemberIds}, `member_mgr_ids` = #{memberMgrIds}, `yaoqing_able` = #{yaoqingAble}, `yaoqing_audit_able` = #{yaoqingAuditAble}, `use_custom_headpic` = #{useCustomHeadpic}
WHERE id = #{id} and org_id = #{orgId}
