mapper.select:
SELECT o.name orgName, d.domain_id domainId, o.org_id orgId, http_domain httpDomain, ws_domain wsDomain, oss_domain ossDomain, admin_domain adminDomain FROM sys_org o LEFT JOIN domains d ON o.org_id = d.org_id where 1=1
AND org_id = #{orgId}
AND http_domain like concat('%',#{httpDomain},'%') or admin_domain like concat('%',#{httpDomain},'%')

mapper.update:
UPDATE domains

    http_domain = #{http_domain},
    ws_domain = #{ws_domain},
    oss_domain = #{oss_domain},
    admin_domain = #{admin_domain}

where domain_id = #{domain_id}
