mapper.select:
SELECT o.name org_name, d.domain_id domain_id, o.org_id org_id, http_domain, ws_domain, oss_domain, admin_domain FROM sys_org o LEFT JOIN domains d ON o.org_id = d.org_id where 1=1
AND org_id = #{org_id}
AND http_domain like concat('%',#{http_domain},'%') or admin_domain like concat('%',#{http_domain},'%')

mapper.update:
UPDATE domains

    http_domain = #{http_domain},
    ws_domain = #{ws_domain},
    oss_domain = #{oss_domain},
    admin_domain = #{admin_domain}

where domain_id = #{domain_id}
